import React, { useRef, useState } from "react";
import './TicTacToe.css'
import x_icon from './cross.png'
import o_icon from './circle.png'

let data = ["","","","","","","","",""];

const TicTacToe = () => {

    let [count, setcount] = useState(0);        
    let [lock, setlock] = useState(false);
    let titleref = useRef(null);

    const toggle = (e, num) => {

        console.log("clicked...")

        if(lock){
            return 0;
        }

        if(num % 2 === 0){
            e.target.innerHTML = `<img src='${x_icon}'>`;   
            data[num] = "x";
            setcount(++count);

        } else {
            e.target.innerHTML = `<img src='${o_icon}'>`;
            data[num] = "o";
            setcount(++count);
        }

        checkwin()
    }
    const checkwin = () => {

        if(data[0] === data[1] && data[1] === data[2] && data[2] !==""){
            win(data[2]);

        }else if(data[3] === data[4] && data[4] === data[5] && data[5] !=="") {
            win(data[5]);

        }else if(data[6] === data[7] && data[7] === data[8] && data[8] !=="") {
            win(data[8]);

        }else if(data[1] === data[4] && data[4] === data[7] && data[7] !=="") {
            win(data[7]);

        }else if(data[2] === data[5] && data[5] === data[8] && data[8] !=="") {
            win(data[8]);

        }else if(data[0] === data[4] && data[4] === data[8] && data[8] !=="") {
            win(data[8]);

        }else if(data[0] === data[1] && data[1] === data[2] && data[2] !=="") {
            win(data[2]);

        }else if(data[2] === data[4] && data[4] === data[6] && data[6] !=="") {
            win(data[6]);

        }
    }

    const win = (winner) => {
        setlock(true)
        if(winner === "x") {
            titleref.current.innerHTML = `<img src='${x_icon}'>: Wins`;
        } else {
            titleref.current.innerHTML = `<img src='${o_icon}'>: Wins`;
        }
    }

    const reset = () => {
        setlock(false);
        let data = ["","","","","","","","",""];
        titleref.current.innerHTML
    }

    return(
        <div className="container">
            <h1 className="title" ref = {titleref}>Tic Tac Toe Game</h1>
            <div className="board">
                <div className="row1">
                    <div className="boxes" onClick={(e)=>{toggle(e, 0)}}></div>
                    <div className="boxes" onClick={(e)=>{toggle(e, 1)}}></div>
                    <div className="boxes" onClick={(e)=>{toggle(e, 2)}}></div>
                </div>
                <div className="row2">
                    <div className="boxes" onClick={(e)=>{toggle(e, 3)}}></div>
                    <div className="boxes" onClick={(e)=>{toggle(e, 4)}}></div>
                    <div className="boxes" onClick={(e)=>{toggle(e, 5)}}></div>
                </div>
                <div className="row3">
                    <div className="boxes" onClick={(e)=>{toggle(e, 6)}}></div>
                    <div className="boxes" onClick={(e)=>{toggle(e, 7)}}></div>
                    <div className="boxes" onClick={(e)=>{toggle(e, 8)}}></div>
                </div>
            </div>
            <button className="reset">Reset</button>
        </div>
    )
}

export default TicTacToe
